INSERT INTO `ai_model_config` (`id`, `model_type`, `model_code`, `model_name`, `is_default`, `is_enabled`, `config_json`, `doc_link`, `remark`, `sort`, `creator`, `create_date`, `updater`, `update_date`) VALUES ('Intent_intent_llm','intent','intent_llm','LLM intent recognition',0,1,'{\"llm\": \"LLM_ChatGLMLLM\", \"type\": \"intent_llm\"}',NULL,'LLM Intent Recognition Configuration Instructions:\n1. Use a standalone LLM for intent recognition.\n2. The model in selected_module.LLM is used by default.\n3. You can configure a standalone LLM (such as the free ChatGLMLLM).\n4. This is highly versatile but increases processing time.\nConfiguration Instructions:\n1. Specify the LLM model to use in the llm field.\n2. If not specified, the model in selected_module.LLM is used.',2,NULL,NULL,1968954060498972674,'2025-09-20 19:24:13');
INSERT INTO `ai_model_config` (`id`, `model_type`, `model_code`, `model_name`, `is_default`, `is_enabled`, `config_json`, `doc_link`, `remark`, `sort`, `creator`, `create_date`, `updater`, `update_date`) VALUES ('Intent_nointent','intent','nointent','No intent to identify',0,0,'{\"type\": \"nointent\"}',NULL,'Configuration Instructions for Non-Intent Recognition:\n1. No intent recognition performed\n2. All conversations are directly passed to LLM for processing\n3. No additional configuration required\n4. Suitable for simple conversation scenarios',1,NULL,NULL,1968954060498972674,'2025-09-20 19:24:13');
INSERT INTO `ai_model_config` (`id`, `model_type`, `model_code`, `model_name`, `is_default`, `is_enabled`, `config_json`, `doc_link`, `remark`, `sort`, `creator`, `create_date`, `updater`, `update_date`) VALUES ('LLM_GroqLLM','llm','GroqLLM','Groq LLM Chatgpt',1,1,'{\"type\": \"openai\", \"top_k\": \"\", \"top_p\": \"1\", \"api_key\": \"YOUR_GROQ_API_KEY\", \"timeout\": 15, \"base_url\": \"https://api.groq.com/openai/v1\", \"max_tokens\": \"2048\", \"model_name\": \"openai/gpt-oss-20b\", \"max_retries\": 2, \"retry_delay\": 1, \"temperature\": \"0.7\", \"frequency_penalty\": \"0\"}','https://console.groq.com/','Groq LLM Configuration Guide:\n1. Groq is an AI chip company focused on high-performance inference, providing fast LLM inference services\n2. Supports various open-source large language models like Llama, Mixtral, etc.\n3. Features ultra-low latency inference performance, suitable for real-time conversation scenarios\n4. Uses OpenAI-compatible API interface for easy integration\n5. Requires API key from Groq official website\n\nConfiguration Parameters:\n- api_key: API key obtained from Groq console\n- model_name: Model name, e.g., llama3-8b-8192, mixtral-8x7b-32768, etc.\n- base_url: Groq API endpoint, typically https://api.groq.com/openai/v1\n- temperature: Controls output randomness (0-2), lower values are more deterministic\n- max_tokens: Maximum tokens to generate per request\n- top_p: Nucleus sampling parameter controlling output diversity\n- frequency_penalty: Frequency penalty to reduce repetitive content\n- timeout: Request timeout in seconds, recommended 15s (Groq is fast)\n- max_retries: Maximum retry attempts, recommended 2\n- retry_delay: Retry interval in seconds, recommended 1s\n\nGet API Key: https://console.groq.com/keys\nModel List: https://console.groq.com/docs/models\n',16,NULL,NULL,1968954060498972674,'2025-09-25 19:41:54');
INSERT INTO `ai_model_config` (`id`, `model_type`, `model_code`, `model_name`, `is_default`, `is_enabled`, `config_json`, `doc_link`, `remark`, `sort`, `creator`, `create_date`, `updater`, `update_date`) VALUES ('Memory_mem_local_short','memory','mem_local_short','local short term memory',0,1,'{\"llm\": \"LLM_ChatGLMLLM\", \"type\": \"mem_local_short\"}',NULL,'Local Short-Term Memory Configuration Instructions:\n1. Use local storage to save conversation history\n2. Summarize conversation content through the selected_module\'s LLM\n3. Data is stored locally and not uploaded to the server\n4. Suitable for privacy-conscious scenarios\n5. No additional configuration required',2,NULL,NULL,1968954060498972674,'2025-09-19 18:12:50');
