-- Local short-term memory can set independent LLM
UPDATE ai_model_provider SET fields =  '[{"key":"llm","label":"LLM Model","type":"string"}]' WHERE  id = 'SYSTEM_Memory_mem_local_short';
UPDATE ai_model_config SET config_json =  '{"type": "mem_local_short", "llm": "LLM_ChatGLMLLM"}' WHERE  id = 'Memory_mem_local_short';

-- Add Huoshan Double Stream TTS provider and model configuration
DELETE FROM ai_model_provider WHERE id = 'SYSTEM_TTS_HSDSTTS';

DELETE FROM ai_model_config WHERE id = 'TTS_HuoshanDoubleStreamTTS';

-- Huoshan Double Stream TTS model configuration documentation
UPDATE ai_model_config SET
doc_link = 'https://console.volcengine.com/speech/service/10007',
remark = 'Huoshan TTS service configuration instructions:
1. Visit https://www.volcengine.com/ to register and activate Huoshan Engine account
2. Visit https://console.volcengine.com/speech/service/10007 to activate TTS large model service, purchase voices
3. Get appid and access_token at bottom of page
5. Resource ID fixed as: volc.service_type.10029 (Large model TTS and mixing)
6. Fill into configuration file' WHERE id = 'TTS_HuoshanDoubleStreamTTS';


-- Add Huoshan Double Stream TTS voices
DELETE FROM ai_tts_voice WHERE tts_model_id = 'TTS_HuoshanDoubleStreamTTS';
