-- Create content_items table for storing music and story content
-- Supports multiple content types with metadata and file references

CREATE TABLE IF NOT EXISTS `content_items` (
  `id` CHAR(36) NOT NULL COMMENT 'Unique identifier (UUID)',
  `title` TEXT NOT NULL COMMENT 'Display title',
  `romanized` TEXT NULL COMMENT 'Romanized version of title',
  `filename` TEXT NOT NULL COMMENT 'Audio filename',
  `content_type` VARCHAR(50) NOT NULL COMMENT 'Type: music or story',
  `category` VARCHAR(100) NOT NULL COMMENT 'Content category',
  `alternatives` JSON NULL COMMENT 'Alternative titles/spellings',
  `file_url` TEXT NULL COMMENT 'File storage URL',
  `duration_seconds` INT NULL COMMENT 'Duration in seconds',
  `created_at` DATETIME NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Creation timestamp',
  `updated_at` DATETIME NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Last update timestamp',
  `thumbnail_url` VARCHAR(500) NULL COMMENT 'Thumbnail image URL',
  PRIMARY KEY (`id`),
  CHECK (`content_type` IN ('music', 'story'))
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COMMENT='Content library for music and stories';

-- Create indexes for common queries
CREATE INDEX `idx_content_items_content_type` ON `content_items`(`content_type`);
CREATE INDEX `idx_content_items_category` ON `content_items`(`category`);
CREATE INDEX `idx_content_items_created_at` ON `content_items`(`created_at`);

